commandDefinition.command:
#!/usr/bin/perl

use strict;

use Getopt::Long;
use WFAUtil;
my $Aggr_Threshold;
GetOptions(
   "Aggr_Threshold=s" =>\$Aggr_Threshold
  ) or die 'Illegal command parameters\n';

my $FileName;

my $FileNamePrefix = 'c:\Aggregate_Report';

$FileName = $FileNamePrefix.".csv";

my $wfa_util = WFAUtil->new();
$wfa_util->sendLog('INFO', 'Creating CSV file: ' . $FileName);

my $query="SELECT
    node.name ,aggr.name,ceil(aggr.size_mb/1024),ceil(aggr.used_size_mb /1024),
    ceil(aggr.available_size_mb/1024),
    ceil(((aggr.used_size_mb )/ aggr.size_mb) * 100) AS aggregate_used_percentage    
FROM
       cm_storage.aggregate AS aggr,
       cm_storage.node AS node

WHERE aggr.node_id = node.id AND
    (
        (
            aggr.used_size_mb
        ) / aggr.size_mb
    ) * 100 >=  '${Aggr_Threshold}' ORDER BY aggregate_used_percentage DESC";

my $database="cm_storage";
my @data;
my $length;

eval {
@data = $wfa_util->invokeMySqlQuery($query,$database);

};

$length = @data;

open(FILE, ">$FileName");
print FILE "Node Name,Aggr Name,Aggr Size (GB),Aggr Used  (GB),Aggr Available (GB),Aggr Used Percent(%)\n";
for (my $index =0; $index < $length; $index++)
{
if ($data[$index] ne "\n")
{
if ($data[$index] eq "")
{
print FILE "-,";
}
else {
print FILE "$data[$index]" . ",";
}
}
else
{
print FILE $data[$index];
}
}
close(FILE);
